sys_script_client.script:
function onSubmit() {

// 	Getting the request type and requested resource from the form..
	var rType = g_form.getValue('u_request_type');
	var rRes = g_form.getValue('u_what_needed');
// 	alert(rRes + ' ' + rType);
	if(rType == 'hr')
	if((rRes == 'hr1' || rRes == 'hr2')){
		return true;
	}
	else{
			alert('Choice mismatch in ResourceType with Required Resource');
			return false;
	}
}
